// Copyright 2018 Stichting BlockMedx

pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/access/Whitelist.sol";
import "openzeppelin-solidity/contracts/token/ERC20/MintableToken.sol";

/// @title Crowdsale for MDX tokens
/// @author Stichting BlockMedx
contract MDXCrowdsale is Whitelist {

    using SafeMath for uint256;

    event TokenPurchase(
        address indexed purchaser,
        address indexed beneficiary,
        uint256 value,
        uint256 amount
    );

    uint private constant SALE_PHASE_TIME = 2 weeks;
    uint8 private constant NUM_PHASES = 3;

    // Purchaseable amounts
    uint256 private constant PRESALE_AMOUNT = 44000000 ether;
    uint256 private constant PHASE1_AMOUNT = 15500000 ether;
    uint256 private constant PHASE2_AMOUNT = 1500800 ether;

    // Phase MDX per 10 wei
    uint256 private constant PRESALE_MDX_PER_WEI_AMT = 176;
    uint256 private constant PHASE1_MDX_PER_WEI_AMT = 124;
    uint256 private constant PHASE2_MDX_PER_WEI_AMT = 112;

    uint private constant WEI_PER_MDX_AMT = 10;

    // Constructor constant state variables
    address private wallet;
    MintableToken private token;

    // State variables
    uint private phaseStartTime;
    uint8 private phase = 0;
    uint256 private mdxRemaining;

    // Public variables
    uint256 public weiRaised;
    uint256 public tokensSold;

    // Note that the rate parameter is unused due to seperate price logic
    constructor(uint _start, address _wallet, MintableToken _token) public {
        wallet = _wallet;
        token = _token;
        phaseStartTime = _start;
        mdxRemaining = _getPhaseTotalMdxAmt();
    }

    /// @notice Buys MDX tokens from sent WAN and provides them to the sender.
    function() external payable {
        buyTokens(msg.sender);
    }

    /// @notice Buys MDX tokens from sent WAN.
    /// @param _to The recipient address for the MDX tokens
    function buyTokens(address _to) public payable onlyIfWhitelisted(_to) {

        uint256 weiAmount = msg.value;

        require(weiAmount > 0);
        require(now >= phaseStartTime);

        _movePhaseAccordingToTime();
        uint256 tokens = _collectMdxToBuy(weiAmount);

        token.mint(_to, tokens);
        wallet.transfer(weiAmount);

        tokensSold = tokensSold.add(tokens);
        weiRaised = weiRaised.add(weiAmount);

        emit TokenPurchase(msg.sender, _to, weiAmount, tokens);

    }

    /// @notice Gets the total amount of tokens remaining available for sale.
    /// @return The amount of remaining tokens in units.
    function totalRemainingTokens() public view returns (uint256 remaining) {

        uint realPhase = phase + _getPhaseMovement();

        remaining += roundRemainingTokens();

        if (realPhase < 1)
            remaining += PHASE1_AMOUNT;

        if (realPhase < 2)
            remaining += PHASE2_AMOUNT;

    }
    /// @notice Gets the amount of tokens remaining available for sale in the
    /// current round.
    /// @return The amount of remaining tokens in units.
    function roundRemainingTokens() public view returns (uint256 remaining) {

        uint movement = _getPhaseMovement();

        if (movement == 0)
            return mdxRemaining;

        uint realPhase = phase + movement;

        if (realPhase == 1)
            return PHASE1_AMOUNT;

        if (realPhase == 2)
            return PHASE2_AMOUNT;

        return 0;

    }

    // Gets the amount of MDX per 10 wei
    function _getPhaseMdxPerWeiAmt() internal view returns (uint) {

        if (phase == 0)
            return PRESALE_MDX_PER_WEI_AMT;
        if (phase == 1)
            return PHASE1_MDX_PER_WEI_AMT;

        assert(phase == 2);
        return PHASE2_MDX_PER_WEI_AMT;

    }

    // Get total available MDX of phase
    function _getPhaseTotalMdxAmt() internal view returns (uint) {

        if (phase == 0)
            return PRESALE_AMOUNT;
        if (phase == 1)
            return PHASE1_AMOUNT;
        if (phase == 2)
            return PHASE2_AMOUNT;

        return 0;

    }

    function _movePhaseAccordingToTime() internal {
        // If the current time is past the current phase, move onto the correct
        // phase

        uint toMove = _getPhaseMovement();

        require(phase + toMove < NUM_PHASES);

        if (toMove > 0) {
            phase += uint8(toMove);
            phaseStartTime += SALE_PHASE_TIME * toMove;
            mdxRemaining = _getPhaseTotalMdxAmt();
        }

    }

    function _collectMdxToBuy(
        uint256 _weiAmount
    ) internal returns (uint256 mdxBought) {

        while (_weiAmount > 0) {
            // Obtain tokens until wei is spent

            // We can only purchase tokens when we are within one of the
            // available phases
            require(phase < NUM_PHASES);

            uint toBuy = _getPhaseMdxAmount(_weiAmount);

            uint weiUsed;

            if (toBuy > mdxRemaining) {
                // Limit the amount that can be bought in this phase
                weiUsed = _getPhaseWeiAmount(mdxRemaining);
                toBuy = _getPhaseMdxAmount(weiUsed);
            } else
                // Managed to use up remaining wei in this phase
                weiUsed = _weiAmount;

            mdxRemaining = mdxRemaining.sub(toBuy);

            if (mdxRemaining < _getPhaseMdxAmount(1)) {
                // Purchased all tokens in this phase or the amount remaining is
                // too small to be purchased, so move to next phase
                phase++;
                phaseStartTime = now;
                mdxRemaining = _getPhaseTotalMdxAmt();
            }

            mdxBought = mdxBought.add(toBuy);
            _weiAmount = _weiAmount.sub(weiUsed);

        }

    }

    function _getPhaseMovement() internal view returns (uint) {

        if (now < phaseStartTime)
            return 0;

        return (now - phaseStartTime) / SALE_PHASE_TIME;

    }

    function _getPhaseMdxAmount(
        uint256 _weiAmount
    ) internal view returns (uint256) {
        return _weiAmount.mul(_getPhaseMdxPerWeiAmt()).div(WEI_PER_MDX_AMT);
    }

    function _getPhaseWeiAmount(
        uint256 _tokens
    ) internal view returns (uint256) {
        return _tokens.mul(WEI_PER_MDX_AMT).div(_getPhaseMdxPerWeiAmt());
    }

}

