// Copyright 2018 Stichting BlockMedx

pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/access/Whitelist.sol";
import "openzeppelin-solidity/contracts/token/ERC20/MintableToken.sol";

/// @title Crowdsale for MDX tokens
/// @author Stichting BlockMedx
contract MDXCrowdsale is Whitelist {

    using SafeMath for uint256;

    event TokenPurchase(
        address indexed purchaser,
        address indexed beneficiary,
        uint256 value,
        uint256 amount
    );

    uint private constant SALE_PHASE_TIME = 2 weeks;
    uint8 private constant NUM_PHASES = 3;
    uint256 private constant MIN_PURCHASE = 100 ether;

    // Purchaseable amounts
    uint256 private constant PRESALE_AMOUNT = 44000000 ether;
    uint256 private constant PHASE1_AMOUNT = 15500000 ether;
    uint256 private constant PHASE2_AMOUNT = 1500800 ether;

    // Round MDX per 10 wei
    uint256 private constant PRESALE_MDX_PER_WEI_AMT = 176;
    uint256 private constant PHASE1_MDX_PER_WEI_AMT = 124;
    uint256 private constant PHASE2_MDX_PER_WEI_AMT = 112;

    uint private constant WEI_PER_MDX_AMT = 10;

    // Constructor constant state variables
    address private wallet;
    MintableToken private token;

    // State variables
    uint private roundStartTime;
    uint8 private round = 0;
    uint256 private mdxRemaining;

    // Public variables
    uint256 public weiRaised;
    uint256 public tokensSold;

    // Note that the rate parameter is unused due to seperate price logic
    constructor(uint _start, address _wallet, MintableToken _token) public {
        wallet = _wallet;
        token = _token;
        roundStartTime = _start;
        mdxRemaining = _getRoundTotalMdxAmt();
    }

    /// @notice Buys MDX tokens from sent WAN and provides them to the sender.
    function() external payable {
        buyTokens(msg.sender);
    }

    /// @notice Buys MDX tokens from sent WAN.
    /// @param _to The recipient address for the MDX tokens
    function buyTokens(address _to) public payable onlyIfWhitelisted(_to) {

        uint256 weiAmount = msg.value;

        require(weiAmount >= MIN_PURCHASE, "Minimum purchase is 100 WAN");
        require(now >= roundStartTime, "The crowdsale has not started");

        _moveRoundAccordingToTime();
        uint256 tokens = _collectMdxToBuy(weiAmount);

        token.mint(_to, tokens);
        wallet.transfer(weiAmount);

        tokensSold = tokensSold.add(tokens);
        weiRaised = weiRaised.add(weiAmount);

        emit TokenPurchase(msg.sender, _to, weiAmount, tokens);

    }

    /// @notice Gets the total amount of tokens remaining available for sale.
    /// @return The amount of remaining tokens in units.
    function totalRemainingTokens() public view returns (uint256 remaining) {

        uint realRound = round + _getRoundMovement();

        remaining += roundRemainingTokens();

        if (realRound < 1)
            remaining += PHASE1_AMOUNT;

        if (realRound < 2)
            remaining += PHASE2_AMOUNT;

    }

    /// @notice Gets the amount of tokens remaining available for sale in the
    /// current round.
    /// @return The amount of remaining tokens in units.
    function roundRemainingTokens() public view returns (uint256) {

        uint movement = _getRoundMovement();

        if (movement == 0)
            return mdxRemaining;

        uint realRound = round + movement;

        if (realRound == 1)
            return PHASE1_AMOUNT;

        if (realRound == 2)
            return PHASE2_AMOUNT;

        return 0;

    }

    /// @notice Gets the amount of seconds left in the current round
    /// @return The amount of time remaining for the current round in seconds
    function roundRemainingTime() public view returns (uint256) {

        uint movement = _getRoundMovement();

        if (round + movement >= NUM_PHASES)
            return 0;

        uint256 endTime = roundStartTime + SALE_PHASE_TIME * (movement + 1);

        return endTime - now;

    }

    /// @notice Gets the current round
    /// @return The current round starting from 0 (presale)
    function currentRound() public view returns (uint8) {

        uint movement = _getRoundMovement();

        if (round + movement >= NUM_PHASES)
            return NUM_PHASES;

        return round + uint8(movement);

    }

    /// @notice Atomically obtains the current crowdsale status
    /// @return Total remaining tokens, tokens remaining in round, time
    /// remaining in round and the current round number
    function getInfo() public view returns (
        uint256 totalRemaining,
        uint256 roundRemaining,
        uint256 roundTimeRemaining,
        uint8 current
    ) {
        totalRemaining = totalRemainingTokens();
        roundRemaining = roundRemainingTokens();
        roundTimeRemaining = roundRemainingTime();
        current = currentRound();
    }

    // Gets the amount of MDX per 10 wei
    function _getRoundMdxPerWeiAmt() internal view returns (uint) {

        if (round == 0)
            return PRESALE_MDX_PER_WEI_AMT;
        if (round == 1)
            return PHASE1_MDX_PER_WEI_AMT;

        assert(round == 2);
        return PHASE2_MDX_PER_WEI_AMT;

    }

    // Get total available MDX of round
    function _getRoundTotalMdxAmt() internal view returns (uint) {

        if (round == 0)
            return PRESALE_AMOUNT;
        if (round == 1)
            return PHASE1_AMOUNT;
        if (round == 2)
            return PHASE2_AMOUNT;

        return 0;

    }

    function _moveRoundAccordingToTime() internal {
        // If the current time is past the current round, move onto the correct
        // round

        uint toMove = _getRoundMovement();

        require(round + toMove < NUM_PHASES, "The crowdsale has ended");

        if (toMove > 0) {
            round += uint8(toMove);
            roundStartTime += SALE_PHASE_TIME * toMove;
            mdxRemaining = _getRoundTotalMdxAmt();
        }

    }

    function _collectMdxToBuy(
        uint256 _weiAmount
    ) internal returns (uint256 mdxBought) {

        while (_weiAmount > 0) {
            // Obtain tokens until wei is spent

            // We can only purchase tokens when we are within one of the
            // available rounds
            require(round < NUM_PHASES, "No tokens remaining to purchase");

            uint toBuy = _getRoundMdxAmount(_weiAmount);

            uint weiUsed;

            if (toBuy > mdxRemaining) {
                // Limit the amount that can be bought in this round
                toBuy = mdxRemaining;
                weiUsed = _getRoundWeiAmount(mdxRemaining);
            } else
                // Managed to use up remaining wei in this round
                weiUsed = _weiAmount;

            mdxRemaining = mdxRemaining.sub(toBuy);

            if (mdxRemaining < _getRoundMdxAmount(1)) {
                // Purchased all tokens in this round or the amount remaining is
                // too small to be purchased, so move to next round
                round++;
                roundStartTime = now;
                mdxRemaining = _getRoundTotalMdxAmt();
            }

            mdxBought = mdxBought.add(toBuy);
            _weiAmount = _weiAmount.sub(weiUsed);

        }

    }

    function _getRoundMovement() internal view returns (uint) {

        if (now < roundStartTime)
            return 0;

        return (now - roundStartTime) / SALE_PHASE_TIME;

    }

    function _getRoundMdxAmount(
        uint256 _weiAmount
    ) internal view returns (uint256) {
        return _weiAmount.mul(_getRoundMdxPerWeiAmt()).div(WEI_PER_MDX_AMT);
    }

    function _getRoundWeiAmount(
        uint256 _tokens
    ) internal view returns (uint256) {
        return _tokens.mul(WEI_PER_MDX_AMT).div(_getRoundMdxPerWeiAmt());
    }

}

